"""
Category Manager for Budget Tracker
Handles loading, managing, and updating categories and subcategories
"""

import csv
import os
import sqlite3
from typing import Dict, List, Optional, Set
from database.connection import get_db_connection

class CategoryManager:
    """Centralized manager for categories and subcategories"""

    def __init__(self):
        self._categories_data = {}
        self._load_categories()
        self._ensure_categories_table()
        self._sync_with_database()

    def _load_categories(self) -> None:
        """Load categories from the categories.csv file"""
        # Try to find categories.csv in common locations
        possible_paths = [
            '/Users/jeffreywooster/Documents/Development/6_Budget_Master/categories.csv',
            os.path.join(os.path.dirname(__file__), '..', 'categories.csv'),
            os.path.join(os.path.dirname(__file__), '..', '..', 'categories.csv'),
            'categories.csv'
        ]

        categories_file = None
        for path in possible_paths:
            if os.path.exists(path):
                categories_file = path
                break

        if categories_file:
            try:
                with open(categories_file, 'r', encoding='utf-8') as file:
                    reader = csv.DictReader(file)
                    for row in reader:
                        category = row.get('Category', '').strip()
                        subcategory = row.get('Sub Category', '').strip()

                        if category and subcategory:
                            if category not in self._categories_data:
                                self._categories_data[category] = []
                            if subcategory not in self._categories_data[category]:
                                self._categories_data[category].append(subcategory)

                print(f"Loaded {len(self._categories_data)} categories from {categories_file}")

            except Exception as e:
                print(f"Error loading categories from CSV: {e}")
                self._load_default_categories()
        else:
            print("Categories.csv not found, using default categories")
            self._load_default_categories()

    def _load_default_categories(self) -> None:
        """Load default categories as fallback"""
        self._categories_data = {
            'Housing': ['Mortgage', 'HOA', 'Property Taxes', 'Reserves'],
            'Utilities': ['Electric', 'Gas', 'Internet', 'Phone', 'Insurance'],
            'Food': ['Food (Groceries)', 'Food (Take Out)', 'Food (Dining Out)'],
            'Healthcare': ['Prescriptions', 'Doctor Visits', 'Co-Pay'],
            'Vehicles': ['Gas', 'Insurance', 'Repairs', 'Parking'],
            'Other': ['Entertainment', 'Clothes', 'Other']
        }

    def _ensure_categories_table(self) -> None:
        """Ensure the categories table exists in the database"""
        try:
            with get_db_connection() as db:
                db.execute('''
                    CREATE TABLE IF NOT EXISTS categories (
                        id INTEGER PRIMARY KEY AUTOINCREMENT,
                        category TEXT NOT NULL,
                        subcategory TEXT NOT NULL,
                        created_date DATE DEFAULT CURRENT_DATE,
                        UNIQUE(category, subcategory)
                    )
                ''')
                db.commit()
        except Exception as e:
            print(f"Error creating categories table: {e}")

    def _sync_with_database(self) -> None:
        """Sync categories with database and load any custom categories"""
        try:
            with get_db_connection() as db:
                # First, insert all CSV categories into database if they don't exist
                for category, subcategories in self._categories_data.items():
                    for subcategory in subcategories:
                        try:
                            db.execute('''
                                INSERT OR IGNORE INTO categories (category, subcategory)
                                VALUES (?, ?)
                            ''', (category, subcategory))
                        except Exception as e:
                            print(f"Error inserting category {category}/{subcategory}: {e}")

                # Then load any additional categories from database
                db_categories = db.execute('''
                    SELECT category, subcategory FROM categories
                    ORDER BY category, subcategory
                ''').fetchall()

                for row in db_categories:
                    category = row['category']
                    subcategory = row['subcategory']

                    if category not in self._categories_data:
                        self._categories_data[category] = []
                    if subcategory not in self._categories_data[category]:
                        self._categories_data[category].append(subcategory)

                db.commit()

        except Exception as e:
            print(f"Error syncing with database: {e}")

    def get_categories(self) -> Dict[str, List[str]]:
        """Get all categories and subcategories"""
        return self._categories_data.copy()

    def get_category_names(self) -> List[str]:
        """Get list of all category names"""
        return sorted(self._categories_data.keys())

    def get_subcategories(self, category: str) -> List[str]:
        """Get subcategories for a specific category"""
        return self._categories_data.get(category, []).copy()

    def add_category(self, category: str) -> bool:
        """Add a new category"""
        if not category or category in self._categories_data:
            return False

        try:
            with get_db_connection() as db:
                # Add category with a default subcategory
                default_subcategory = f"{category} (General)"
                db.execute('''
                    INSERT INTO categories (category, subcategory)
                    VALUES (?, ?)
                ''', (category, default_subcategory))
                db.commit()

                self._categories_data[category] = [default_subcategory]
                return True

        except Exception as e:
            print(f"Error adding category {category}: {e}")
            return False

    def add_subcategory(self, category: str, subcategory: str) -> bool:
        """Add a new subcategory to an existing category"""
        if not category or not subcategory:
            return False

        # Create category if it doesn't exist
        if category not in self._categories_data:
            self._categories_data[category] = []

        # Check if subcategory already exists
        if subcategory in self._categories_data[category]:
            return False

        try:
            with get_db_connection() as db:
                db.execute('''
                    INSERT INTO categories (category, subcategory)
                    VALUES (?, ?)
                ''', (category, subcategory))
                db.commit()

                self._categories_data[category].append(subcategory)
                return True

        except Exception as e:
            print(f"Error adding subcategory {category}/{subcategory}: {e}")
            return False

    def remove_subcategory(self, category: str, subcategory: str) -> bool:
        """Remove a subcategory (only if not used in expenses)"""
        if category not in self._categories_data or subcategory not in self._categories_data[category]:
            return False

        try:
            with get_db_connection() as db:
                # Check if subcategory is used in expenses
                usage_count = db.execute('''
                    SELECT COUNT(*) as count FROM expenses
                    WHERE category = ? AND subcategory = ?
                ''', (category, subcategory)).fetchone()

                if usage_count and usage_count['count'] > 0:
                    print(f"Cannot remove subcategory {category}/{subcategory} - it's used in {usage_count['count']} expenses")
                    return False

                # Remove from database
                db.execute('''
                    DELETE FROM categories
                    WHERE category = ? AND subcategory = ?
                ''', (category, subcategory))
                db.commit()

                # Remove from memory
                self._categories_data[category].remove(subcategory)

                # Remove category if it has no subcategories
                if not self._categories_data[category]:
                    del self._categories_data[category]

                return True

        except Exception as e:
            print(f"Error removing subcategory {category}/{subcategory}: {e}")
            return False

    def category_exists(self, category: str) -> bool:
        """Check if a category exists"""
        return category in self._categories_data

    def subcategory_exists(self, category: str, subcategory: str) -> bool:
        """Check if a subcategory exists in a category"""
        return category in self._categories_data and subcategory in self._categories_data[category]

    def get_all_subcategories(self) -> Set[str]:
        """Get all subcategories across all categories"""
        all_subcategories = set()
        for subcategories in self._categories_data.values():
            all_subcategories.update(subcategories)
        return all_subcategories

    def search_categories(self, search_term: str) -> Dict[str, List[str]]:
        """Search for categories and subcategories containing the search term"""
        results = {}
        search_lower = search_term.lower()

        for category, subcategories in self._categories_data.items():
            category_matches = search_lower in category.lower()
            matching_subcategories = [
                sub for sub in subcategories
                if search_lower in sub.lower()
            ]

            if category_matches or matching_subcategories:
                if category_matches:
                    results[category] = subcategories.copy()
                else:
                    results[category] = matching_subcategories

        return results

    def refresh(self) -> None:
        """Refresh categories from CSV and database"""
        self._categories_data.clear()
        self._load_categories()
        self._sync_with_database()

# Global instance
_category_manager = None

def get_category_manager() -> CategoryManager:
    """Get the global category manager instance"""
    global _category_manager
    if _category_manager is None:
        _category_manager = CategoryManager()
    return _category_manager

"""
Category Manager for Budget Tracker
Handles loading, managing, and updating categories and subcategories
"""

import csv
import os
import sqlite3
from typing import Dict, List, Optional, Set
from database.db_manager import DatabaseManager

class CategoryManager:
    """Centralized manager for categories and subcategories"""

    def __init__(self):
        self._categories_data = {}
        self._load_categories()
        self._ensure_categories_table()
        self._sync_with_database()

    def _load_categories(self) -> None:
        """Load categories from the categories.csv file with proper encoding handling"""
        # Try to find categories.csv in the current directory first
        possible_paths = [
            os.path.join(os.path.dirname(__file__), '..', 'categories.csv'),
            'categories.csv',
            '/Users/jeffreywooster/Documents/Development/6_Budget_Master/categories.csv',
            os.path.join(os.path.dirname(__file__), '..', '..', 'categories.csv')
        ]

        categories_file = None
        for path in possible_paths:
            if os.path.exists(path):
                categories_file = path
                break

        if categories_file:
            try:
                # Try different encodings to handle potential encoding issues
                encodings = ['utf-8', 'utf-8-sig', 'latin1', 'cp1252']

                for encoding in encodings:
                    try:
                        with open(categories_file, 'r', encoding=encoding) as file:
                            reader = csv.DictReader(file)

                            # Clear existing data
                            self._categories_data = {}

                            for row in reader:
                                category = row.get('Category', '').strip()
                                subcategory = row.get('Sub Category', '').strip()

                                if category and subcategory:
                                    if category not in self._categories_data:
                                        self._categories_data[category] = []
                                    if subcategory not in self._categories_data[category]:
                                        self._categories_data[category].append(subcategory)

                        print(f"Loaded {len(self._categories_data)} categories from {categories_file} using {encoding} encoding")
                        return  # Success, exit the function

                    except UnicodeDecodeError:
                        continue  # Try next encoding

                # If we get here, all encodings failed
                raise Exception("Could not decode file with any supported encoding")

            except Exception as e:
                print(f"Error loading categories from CSV: {e}")
                self._load_default_categories()
        else:
            print("Categories.csv not found, using default categories")
            self._load_default_categories()

    def _load_default_categories(self) -> None:
        """Load default categories as fallback"""
        self._categories_data = {
            'Housing': ['Mortgage', 'HOA', 'Property Taxes', 'Reserves'],
            'Utilities': ['Electric', 'Gas', 'Internet', 'Phone', 'Insurance'],
            'Food': ['Food (Groceries)', 'Food (Take Out)', 'Food (Dining Out)'],
            'Healthcare': ['Prescriptions', 'Doctor Visits', 'Co-Pay'],
            'Vehicles': ['Gas', 'Insurance', 'Repairs', 'Parking'],
            'Other': ['Entertainment', 'Clothes', 'Other'],
            'Income': ["Jeff's Income", "Vanessa's Income", "Bonus", "Other Income"]
        }

    def _ensure_categories_table(self) -> None:
        """Ensure the categories table exists in the database"""
        try:
            with DatabaseManager() as db:
                db.execute('''
                    CREATE TABLE IF NOT EXISTS categories (
                        id INTEGER PRIMARY KEY AUTOINCREMENT,
                        category TEXT NOT NULL,
                        subcategory TEXT NOT NULL,
                        created_date DATE DEFAULT CURRENT_DATE,
                        UNIQUE(category, subcategory)
                    )
                ''')
        except Exception as e:
            print(f"Error creating categories table: {e}")

    def _sync_with_database(self) -> None:
        """Sync categories with database and load any custom categories"""
        try:
            with DatabaseManager() as db:
                # First, insert all CSV categories into database if they don't exist
                for category, subcategories in self._categories_data.items():
                    for subcategory in subcategories:
                        try:
                            db.execute('''
                                INSERT OR IGNORE INTO categories (category, subcategory)
                                VALUES (?, ?)
                            ''', (category, subcategory))
                        except Exception as e:
                            print(f"Error inserting category {category}/{subcategory}: {e}")

                # Then load any additional categories from database
                db_categories = db.execute('''
                    SELECT category, subcategory FROM categories
                    ORDER BY category, subcategory
                ''').fetchall()

                for row in db_categories:
                    category = row['category']
                    subcategory = row['subcategory']

                    if category not in self._categories_data:
                        self._categories_data[category] = []
                    if subcategory not in self._categories_data[category]:
                        self._categories_data[category].append(subcategory)

        except Exception as e:
            print(f"Error syncing with database: {e}")

    def get_categories(self) -> Dict[str, List[str]]:
        """Get all categories and subcategories"""
        return self._categories_data.copy()

    def get_category_names(self) -> List[str]:
        """Get list of all category names"""
        return sorted(self._categories_data.keys())

    def get_subcategories(self, category: str) -> List[str]:
        """Get subcategories for a specific category"""
        return self._categories_data.get(category, []).copy()

    def add_category(self, category: str) -> bool:
        """Add a new category"""
        if not category or category in self._categories_data:
            return False

        try:
            with DatabaseManager() as db:
                # Add category with a default subcategory
                default_subcategory = f"{category} (General)"
                db.execute('''
                    INSERT INTO categories (category, subcategory)
                    VALUES (?, ?)
                ''', (category, default_subcategory))

                self._categories_data[category] = [default_subcategory]
                return True

        except Exception as e:
            print(f"Error adding category {category}: {e}")
            return False

    def add_subcategory(self, category: str, subcategory: str) -> bool:
        """Add a new subcategory to an existing category"""
        if not category or not subcategory:
            return False

        # Create category if it doesn't exist
        if category not in self._categories_data:
            self._categories_data[category] = []

        # Check if subcategory already exists
        if subcategory in self._categories_data[category]:
            return False

        try:
            with DatabaseManager() as db:
                db.execute('''
                    INSERT INTO categories (category, subcategory)
                    VALUES (?, ?)
                ''', (category, subcategory))

                self._categories_data[category].append(subcategory)
                return True

        except Exception as e:
            print(f"Error adding subcategory {category}/{subcategory}: {e}")
            return False

    def remove_subcategory(self, category: str, subcategory: str) -> bool:
        """Remove a subcategory (only if not used in expenses)"""
        if category not in self._categories_data or subcategory not in self._categories_data[category]:
            return False

        try:
            with DatabaseManager() as db:
                # Check if subcategory is used in expenses
                usage_count = db.execute('''
                    SELECT COUNT(*) as count FROM expenses
                    WHERE category = ? AND subcategory = ?
                ''', (category, subcategory)).fetchone()

                if usage_count and usage_count['count'] > 0:
                    print(f"Cannot remove subcategory {category}/{subcategory}: still in use")
                    return False

                # Remove from database
                db.execute('''
                    DELETE FROM categories 
                    WHERE category = ? AND subcategory = ?
                ''', (category, subcategory))

                # Remove from local data
                self._categories_data[category].remove(subcategory)

                # Remove category if it has no subcategories
                if not self._categories_data[category]:
                    del self._categories_data[category]

                return True

        except Exception as e:
            print(f"Error removing subcategory {category}/{subcategory}: {e}")
            return False

    def refresh_from_database(self) -> None:
        """Refresh categories from database"""
        self._categories_data = {}
        self._sync_with_database()

    def refresh(self) -> None:
        """Refresh categories from database (alias for refresh_from_database)"""
        self.refresh_from_database()

    def is_valid_category(self, category: str, subcategory: str) -> bool:
        """Check if a category/subcategory combination is valid"""
        return (category in self._categories_data and
                subcategory in self._categories_data[category])

    def category_exists(self, category: str) -> bool:
        """Check if a category exists"""
        return category in self._categories_data

    def subcategory_exists(self, category: str, subcategory: str) -> bool:
        """Check if a subcategory exists within a category"""
        return (category in self._categories_data and 
                subcategory in self._categories_data[category])

# Global instance
_category_manager = CategoryManager()

def get_category_manager() -> CategoryManager:
    """Get the global category manager instance"""
    return _category_manager

""" Database models and operations """
from datetime import datetime, date
class IncomeModel: """Model for income operations"""
@staticmethod def add(db, date_str, person, amount, source, notes): """Add income entry""" db.execute(''' INSERT INTO income (date, person, amount, source, notes) VALUES (?, ?, ?, ?, ?) ''', (date_str, person, amount, source, notes)) db.commit()
@staticmethod def get_all(db, limit=50): """Get all income entries""" return db.execute(''' SELECT * FROM income ORDER BY date DESC LIMIT ? ''', (limit,)).fetchall()
@staticmethod def get_by_month(db, month_start, month_end): """Get income for a specific month""" return db.execute(''' SELECT * FROM income WHERE date >= ? AND date <= ? ORDER BY date DESC ''', (month_start, month_end)).fetchall()
@staticmethod def get_total_by_month(db, month_start, month_end): """Get total income for a month""" result = db.execute(''' SELECT COALESCE(SUM(amount), 0) as total FROM income WHERE date >= ? AND date <= ? ''', (month_start, month_end)).fetchone() return result['total'] if result else 0
@staticmethod def delete(db, income_id): """Delete income entry""" db.execute('DELETE FROM income WHERE id = ?', (income_id,)) db.commit()
class ExpenseModel: """Model for expense operations"""
@staticmethod def add(db, date_str, person, amount, category, subcategory, description, payment_method): """Add expense entry""" db.execute(''' INSERT INTO expenses (date, person, amount, category, subcategory, description, payment_method) VALUES (?, ?, ?, ?, ?, ?, ?) ''', (date_str, person, amount, category, subcategory, description, payment_method)) db.commit()
@staticmethod def get_all(db, limit=50): """Get all expense entries""" return db.execute(''' SELECT * FROM expenses ORDER BY date DESC LIMIT ? ''', (limit,)).fetchall()
@staticmethod def get_by_month(db, month_start, month_end): """Get expenses for a specific month""" return db.execute(''' SELECT * FROM expenses WHERE date >= ? AND date <= ? ORDER BY date DESC ''', (month_start, month_end)).fetchall()
@staticmethod def get_total_by_month(db, month_start, month_end): """Get total expenses for a month""" result = db.execute(''' SELECT COALESCE(SUM(amount), 0) as total FROM expenses WHERE date >= ? AND date <= ? ''', (month_start, month_end)).fetchone() return result['total'] if result else 0
@staticmethod def get_by_category(db, month_start, month_end): """Get expenses grouped by category""" return db.execute(''' SELECT category, subcategory, SUM(amount) as total FROM expenses WHERE date >= ? AND date <= ? GROUP BY category, subcategory ORDER BY category, subcategory ''', (month_start, month_end)).fetchall()

""" Database models and operations """
from datetime import datetime, date
class IncomeModel: """Model for income operations"""
@staticmethod def add(db, date_str, person, amount, source, notes): """Add income entry""" db.execute(''' INSERT INTO income (date, person, amount, source, notes) VALUES (?, ?, ?, ?, ?) ''', (date_str, person, amount, source, notes)) db.commit()
@staticmethod def get_all(db, limit=50): """Get all income entries""" return db.execute(''' SELECT * FROM income ORDER BY date DESC LIMIT ? ''', (limit,)).fetchall()
@staticmethod def get_by_month(db, month_start, month_end): """Get income for a specific month""" return db.execute(''' SELECT * FROM income WHERE date >= ? AND date <= ? ORDER BY date DESC ''', (month_start, month_end)).fetchall()
@staticmethod def get_total_by_month(db, month_start, month_end): """Get total income for a month""" result = db.execute(''' SELECT COALESCE(SUM(amount), 0) as total FROM income WHERE date >= ? AND date <= ? ''', (month_start, month_end)).fetchone() return result['total'] if result else 0
@staticmethod def delete(db, income_id): """Delete income entry""" db.execute('DELETE FROM income WHERE id = ?', (income_id,)) db.commit()
class ExpenseModel: """Model for expense operations"""
@staticmethod def add(db, date_str, person, amount, category, subcategory, description, payment_method, realized=False): """Add expense entry""" db.execute(''' INSERT INTO expenses (date, person, amount, category, subcategory, description, payment_method, realized) VALUES (?, ?, ?, ?, ?, ?, ?, ?) ''', (date_str, person, amount, category, subcategory, description, payment_method, realized)) db.commit()
@staticmethod def get_all(db, limit=50): """Get all expense entries""" return db.execute(''' SELECT * FROM expenses ORDER BY date DESC LIMIT ? ''', (limit,)).fetchall()
@staticmethod def get_by_month(db, month_start, month_end): """Get expenses for a specific month""" return db.execute(''' SELECT * FROM expenses WHERE date >= ? AND date <= ? ORDER BY date DESC ''', (month_start, month_end)).fetchall()
@staticmethod def get_total_by_month(db, month_start, month_end): """Get total expenses for a month""" result = db.execute(''' SELECT COALESCE(SUM(amount), 0) as total FROM expenses WHERE date >= ? AND date <= ? ''', (month_start, month_end)).fetchone() return result['total'] if result else 0
@staticmethod def get_by_category(db, month_start, month_end): """Get expenses grouped by category""" return db.execute(''' SELECT category, subcategory, SUM(amount) as total FROM expenses WHERE date >= ? AND date <= ? GROUP BY category, subcategory ORDER BY category, subcategory ''', (month_start, month_end)).fetchall()
@staticmethod def get_unrealized_by_person(db, month_start, month_end): """Get unrealized expenses by person for a specific month""" return db.execute(''' SELECT person, COALESCE(SUM(amount), 0) as total FROM expenses WHERE date >= ? AND date <= ? AND realized = 0 GROUP BY person ''', (month_start, month_end)).fetchall()
@staticmethod def get_unrealized_expenses(db, month_start, month_end): """Get all unrealized expenses for a specific month""" return db.execute(''' SELECT * FROM expenses WHERE date >= ? AND date <= ? AND realized = 0 ORDER BY person, date DESC ''', (month_start, month_end)).fetchall()
@staticmethod def mark_as_realized(db, expense_id): """Mark an expense as realized""" db.execute('UPDATE expenses SET realized = 1 WHERE id = ?', (expense_id,)) db.commit()
@staticmethod def mark_as_unrealized(db, expense_id): """Mark an expense as unrealized""" db.execute('UPDATE expenses SET realized = 0 WHERE id = ?', (expense_id,)) db.commit()
@staticmethod def delete(db, expense_id): """Delete expense entry""" db.execute('DELETE FROM expenses WHERE id = ?', (expense_id,)) db.commit()
class NetWorthModel: """Model for net worth operations"""
@staticmethod def add_or_update(db, asset_type, asset_name, value, person): """Add or update asset""" # Check if asset exists existing = db.execute(''' SELECT id FROM net_worth WHERE asset_name = ? AND date = date('now') ''', (asset_name,)).fetchone()
if existing: db.execute(''' UPDATE net_worth SET value = ?, asset_type = ?, person = ? WHERE id = ? ''', (value, asset_type, person, existing['id'])) else: db.execute(''' INSERT INTO net_worth (date, asset_type, asset_name, value, person) VALUES (date('now'), ?, ?, ?, ?) ''', (asset_type, asset_name, value, person)) db.commit()
@staticmethod def get_current(db): """Get current assets""" return db.execute(''' SELECT * FROM net_worth WHERE date = (SELECT MAX(date) FROM net_worth) ORDER BY value DESC ''').fetchall()
@staticmethod def get_total(db): """Get total net worth""" result = db.execute(''' SELECT COALESCE(SUM(value), 0) as total FROM net_worth WHERE date = (SELECT MAX(date) FROM net_worth) ''').fetchone() return result['total'] if result else 0
@staticmethod def delete(db, asset_name): """Delete asset""" db.execute('DELETE FROM net_worth WHERE asset_name = ?', (asset_name,)) db.commit()
class SavingsGoalModel: """Model for savings goal operations"""
@staticmethod def add(db, goal_name, target_amount, target_date, priority): """Add savings goal""" db.execute(''' INSERT INTO savings_goals (goal_name, target_amount, target_date, priority) VALUES (?, ?, ?, ?) ''', (goal_name, target_amount, target_date, priority)) db.commit()
@staticmethod def get_all(db): """Get all savings goals""" return db.execute(''' SELECT * FROM savings_goals ORDER BY priority ''').fetchall()
@staticmethod def update_amount(db, goal_id, amount): """Update goal current amount""" db.execute(''' UPDATE savings_goals SET current_amount = current_amount + ? WHERE id = ? ''', (amount, goal_id)) db.commit()
@staticmethod def delete(db, goal_id): """Delete savings goal""" db.execute('DELETE FROM savings_goals WHERE id = ?', (goal_id,)) db.commit()

